<script setup>
import { ref, onMounted } from "vue";

const logo = ref(null);
const props = defineProps({
  activeClass: {
    type: Boolean,
    default: false,
  },
  mountedAnimation: {
    type: Boolean,
    default: false,
  },
  rotateAnimation: {
    type: Boolean,
    default: true,
  },
});
onMounted(() => {
  doAnimationOnMount();
});

const doAnimationOnMount = () => {
  if (logo.value && props.mountedAnimation) {
    logo.value.addEventListener("mouseenter", () => {
      logo.value.style.transition = "transform 0.4s ease-out";
      logo.value.style.transform = "scale(1.1) rotate(5deg)";
    });

    logo.value.addEventListener("mouseleave", () => {
      logo.value.style.transition = "transform 0.3s ease-in";
      logo.value.style.transform = "scale(1) rotate(0deg)";
    });
  }
};
